mapper.update:
update class_list
set checklength=#{checklength},
    checkexts=#{checkexts}
where userid = #{userid}
  and classtype = #{classtype}
  and checkfolder = #{checkfolder}

mapper.delete:
delete
from class_list
where userid = #{userid}
  and classtype = #{classtype}
  and checkfolder = #{checkfolder}
